    // Frozen Foods
    { id: 'p16', name: 'Frozen Vegetables', category: 'Frozen Foods', location: 'Beach Resort', function: 'Kitchen' },
    { id: 'p17', name: 'Frozen Pizza', category: 'Frozen Foods', location: 'City Center Cafe', function: 'Kitchen' },

    // Bakery
    { id: 'p18', name: 'Sourdough Bread', category: 'Bakery', location: 'Suburban Hotel', function: 'Breakfast' },
    { id: 'p19', name: 'Croissants', category: 'Bakery', location: 'Fine Dining', function: 'Breakfast' },

    // Spices
    { id: 'p20', name: 'Black Pepper', category: 'Spices & Condiments', location: 'Rooftop Bar', function: 'Kitchen' },
  ];

  return {
    products,
    categories: ['Fresh Produce', 'Dairy', 'Meat & Poultry', 'Beverages', 'Pantry Items', 'Frozen Foods', 'Bakery', 'Spices & Condiments'],
    locations: ['Downtown Hotel', 'Airport Restaurant', 'Beach Resort', 'City Center Cafe', 'Suburban Hotel', 'Fine Dining', 'Rooftop Bar'],
    functions: ['Kitchen', 'Beverage', 'Breakfast', 'Bar'],
  };
};

// Create matrix data for D3 chord diagram
export const createChordMatrix = (products, level = 'category') => {
  const nodes = new Set();
  const connections = {};

  products.forEach((product) => {
    let source, target;

    switch (level) {
      case 'category':
        source = 'All Products';
        target = product.category;
        break;
      case 'location':
        source = product.category;
        target = product.location;
        break;
      case 'function':
        source = product.location;
        target = product.function;
        break;
      default:
        source = 'All Products';
        target = product.category;
    }

    nodes.add(source);
    nodes.add(target);

    const key = `${source}|${target}`;
    connections[key] = (connections[key] || 0) + 1;
  });

  const nodeArray = Array.from(nodes);
  const nodeIndex = Object.fromEntries(nodeArray.map((node, i) => [node, i]));

  // Create matrix
  const matrix = Array(nodeArray.length)
    .fill(null)
    .map(() => Array(nodeArray.length).fill(0));

  Object.entries(connections).forEach(([key, count]) => {
    const [source, target] = key.split('|');
    const sourceIdx = nodeIndex[source];
    const targetIdx = nodeIndex[target];
    if (sourceIdx !== undefined && targetIdx !== undefined) {
      matrix[sourceIdx][targetIdx] = count;
    }
  });

  return {
    matrix,
    nodes: nodeArray,
    connections,
  };
};

src/components/Auth/Signup.js.content:
import React, { useState } from 'react';
import { Mail, Lock, Eye, EyeOff, AlertCircle, User } from 'lucide-react';

const Signup = ({ onSignup, onSwitchToLogin }) => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [errors, setErrors] = useState({});
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const validateForm = () => {
    const newErrors = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Name is required';
    } else if (formData.name.trim().length < 2) {
      newErrors.name = 'Name must be at least 2 characters';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(formData.email)) {
      newErrors.email = 'Please enter a valid email address';
    }

    if (!formData.password) {
      newErrors.password = 'Password is required';
    } else if (formData.password.length < 8) {
      newErrors.password = 'Password must be at least 8 characters';
    }

    if (!formData.confirmPassword) {
      newErrors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = 'Passwords do not match';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) return;

    setIsLoading(true);

    setTimeout(() => {
      setIsLoading(false);
      onSignup({ email: formData.email, name: formData.name });
    }, 1000);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-600 via-blue-700 to-indigo-800 flex items-center justify-center p-4">
      <div className="w-full max-w-md">
        <div className="bg-white rounded-2xl shadow-2xl p-8">
          <div className="text-center mb-8">
            <div className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-br from-blue-500 to-indigo-600 rounded-2xl mb-4 shadow-lg">
              <span className="text-3xl">ğŸ¥</span>
            </div>
            <h1 className="text-2xl font-bold text-slate-800">Create Account</h1>
            <p className="text-sm text-slate-500 mt-1">Hospital Pharma Procurement</p>
          </div>

          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1.5">
                Full Name
              </label>
              <div className="relative">
                <User className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
                <input
                  type="text"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className={`w-full pl-10 pr-4 py-3 border rounded-xl focus:outline-none focus:ring-2 transition-all ${
                    errors.name 
                      ? 'border-red-500 focus:ring-red-200' 
                      : 'border-slate-200 focus:ring-blue-200 focus:border-blue-500'
                  }`}
                  placeholder="Enter your full name"
                />
              </div>
              {errors.name && (
                <div className="flex items-center gap-1.5 mt-1.5 text-red-500 text-sm">
                  <AlertCircle className="w-4 h-4" />
                  <span>{errors.name}</span>
                </div>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1.5">
                Email Address
              </label>
              <div className="relative">
                <Mail className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className={`w-full pl-10 pr-4 py-3 border rounded-xl focus:outline-none focus:ring-2 transition-all ${
                    errors.email 
                      ? 'border-red-500 focus:ring-red-200' 
                      : 'border-slate-200 focus:ring-blue-200 focus:border-blue-500'
                  }`}
                  placeholder="Enter your email"
                />
              </div>
              {errors.email && (
                <div className="flex items-center gap-1.5 mt-1.5 text-red-500 text-sm">
                  <AlertCircle className="w-4 h-4" />
                  <span>{errors.email}</span>
                </div>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1.5">
                Password
              </label>
              <div className="relative">
                <Lock className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
                <input
                  type={showPassword ? 'text' : 'password'}
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  className={`w-full pl-10 pr-12 py-3 border rounded-xl focus:outline-none focus:ring-2 transition-all ${
                    errors.password 
                      ? 'border-red-500 focus:ring-red-200' 
                      : 'border-slate-200 focus:ring-blue-200 focus:border-blue-500'
                  }`}
                  placeholder="Create a password"
                />
                <button
                  type="button"
                  onClick={() => setShowPassword(!showPassword)}
                  className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-400 hover:text-slate-600"
                >
                  {showPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                </button>
              </div>
              {errors.password && (
                <div className="flex items-center gap-1.5 mt-1.5 text-red-500 text-sm">
                  <AlertCircle className="w-4 h-4" />
                  <span>{errors.password}</span>
                </div>
              )}
            </div>

            <div>
              <label className="block text-sm font-medium text-slate-700 mb-1.5">
                Confirm Password
              </label>
              <div className="relative">
                <Lock className="absolute left-3 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
                <input
                  type={showConfirmPassword ? 'text' : 'password'}
                  name="confirmPassword"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  className={`w-full pl-10 pr-12 py-3 border rounded-xl focus:outline-none focus:ring-2 transition-all ${
                    errors.confirmPassword 
                      ? 'border-red-500 focus:ring-red-200' 
                      : 'border-slate-200 focus:ring-blue-200 focus:border-blue-500'
                  }`}
                  placeholder="Confirm your password"
                />
                <button
                  type="button"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                  className="absolute right-3 top-1/2 -translate-y-1/2 text-slate-400 hover:text-slate-600"
                >
                  {showConfirmPassword ? <EyeOff className="w-5 h-5" /> : <Eye className="w-5 h-5" />}
                </button>
              </div>
              {errors.confirmPassword && (
                <div className="flex items-center gap-1.5 mt-1.5 text-red-500 text-sm">
                  <AlertCircle className="w-4 h-4" />
                  <span>{errors.confirmPassword}</span>
                </div>
              )}
            </div>

            <button
              type="submit"
              disabled={isLoading}
              className="w-full py-3 bg-gradient-to-r from-blue-600 to-indigo-600 text-white rounded-xl font-semibold hover:from-blue-700 hover:to-indigo-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-all disabled:opacity-70 disabled:cursor-not-allowed mt-6"
            >
              {isLoading ? (
                <span className="flex items-center justify-center gap-2">
                  <svg className="animate-spin h-5 w-5" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" fill="none" />
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                  </svg>
                  Creating account...
                </span>
              ) : (
                'Create Account'
              )}
            </button>
          </form>

          <div className="mt-6 text-center">
            <p className="text-slate-600">
              Already have an account?{' '}
              <button
                onClick={onSwitchToLogin}
                className="text-blue-600 hover:text-blue-700 font-semibold"
              >
                Sign in
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Signup;

src/components/Overview/KPISection.js.content:
import React from 'react';
import { LineChart, Line, XAxis, YAxis, ReferenceLine, ResponsiveContainer, Tooltip } from 'recharts';
import { ArrowUp, ArrowDown, Target } from 'lucide-react';

const KPISection = ({ kpiGraphData }) => {
  const kpis = [
    { key: 'otif', ...kpiGraphData?.otif },
    { key: 'costSaving', ...kpiGraphData?.costSaving },
    { key: 'expiryDuration', ...kpiGraphData?.expiryDuration },
    { key: 'emergencyPurchase', ...kpiGraphData?.emergencyPurchase }
  ];

  return (
    <div className="mt-8">
      <h3 className="text-lg font-bold text-slate-800 mb-4">Key Performance Indicators</h3>
      <div className="grid grid-cols-2 gap-4">
        {kpis.map((kpi) => {
          if (!kpi.label) return null;

          const isPositive = kpi.key === 'otif' || kpi.key === 'costSaving' 
            ? kpi.change > 0 
            : kpi.change < 0;

          return (
            <div
              key={kpi.key}
              className="bg-white rounded-xl border border-slate-200 p-4 hover:shadow-md transition-all"
            >
              <div className="flex items-start justify-between mb-3">
                <div>
                  <h4 className="text-sm font-semibold text-slate-700">{kpi.label}</h4>
                  <div className="flex items-baseline gap-2 mt-1">
                    <span className="text-2xl font-bold" style={{ color: kpi.color }}>
                      {kpi.current?.toFixed(1)}{kpi.unit}
                    </span>
                    <div className={`flex items-center gap-0.5 text-xs px-1.5 py-0.5 rounded ${
                      isPositive ? 'bg-green-100 text-green-700' : 'bg-red-100 text-red-700'
                    }`}>
                      {isPositive ? <ArrowUp className="w-3 h-3" /> : <ArrowDown className="w-3 h-3" />}
                      {Math.abs(kpi.change || 0).toFixed(1)}{kpi.unit}
                    </div>
                  </div>
                </div>
                <div className="flex items-center gap-1 text-xs text-slate-500">
                  <Target className="w-3 h-3" />
                  <span>Goal: {kpi.goal}{kpi.unit}</span>
                </div>
              </div>

              <div className="h-24">
                <ResponsiveContainer width="100%" height="100%">
                  <LineChart data={kpi.data || []} margin={{ top: 5, right: 5, bottom: 5, left: 5 }}>
                    <XAxis 
                      dataKey="name" 
                      tick={{ fontSize: 10, fill: '#94a3b8' }}
                      axisLine={false}
                      tickLine={false}
                    />
                    <YAxis 
                      hide 
                      domain={[
                        Math.min(kpi.baseline || 0, (kpi.goal || 0) - 10),
                        Math.max((kpi.baseline || 0) + 10, (kpi.goal || 0) + 10)
                      ]}
                    />
                    <Tooltip 
                      contentStyle={{ 
                        backgroundColor: '#1e293b', 
                        border: 'none', 
                        borderRadius: '8px',
                        fontSize: '12px',
                        color: 'white'
                      }}
                      formatter={(value) => [`${value.toFixed(1)}${kpi.unit}`, 'Value']}
                    />
                    <ReferenceLine 
                      y={kpi.goal} 
                      stroke={kpi.color} 
                      strokeDasharray="4 4" 
                      strokeOpacity={0.5}
                    />
                    <Line
                      type="monotone"
                      dataKey="value"
                      stroke={kpi.color}
                      strokeWidth={2}
                      dot={false}
                      activeDot={{ r: 4, fill: kpi.color }}
                    />
                  </LineChart>
                </ResponsiveContainer>
              </div>

              <div className="flex items-center justify-between mt-2 pt-2 border-t border-slate-100 text-xs text-slate-500">
                <span>Baseline: {kpi.baseline}{kpi.unit}</span>
                <span className={isPositive ? 'text-green-600' : 'text-red-600'}>
                  {isPositive ? 'Improving' : 'Needs Attention'}
                </span>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default KPISection;

src/components/Overview/PharmaChodDiagra.content:
import React, { useEffect, useRef, useState, useMemo } from 'react';
import * as d3 from 'd3';
import { generateChordData, pharmaCategories } from '../../data/pharmaChordData';

const PharmaChordDiagram = ({ timeframe = 'daily' }) => {
  const svgRef = useRef();
  const containerRef = useRef();
  const [filterType, setFilterType] = useState('areas');
  const [hoveredItem, setHoveredItem] = useState(null);
  const [dimensions, setDimensions] = useState({ width: 800, height: 500 });

  const chordData = useMemo(() => 
    generateChordData(filterType, timeframe), 
    [filterType, timeframe]
  );

  useEffect(() => {
    const updateDimensions = () => {
      if (containerRef.current) {
        const { width } = containerRef.current.getBoundingClientRect();
        setDimensions({
          width: Math.max(width - 280, 500),
          height: 500
        });
      }
    };
    updateDimensions();
    window.addEventListener('resize', updateDimensions);
    return () => window.removeEventListener('resize', updateDimensions);
  }, []);

  useEffect(() => {
    if (!svgRef.current || !chordData) return;

    const { width, height } = dimensions;
    const margin = { top: 60, right: 160, bottom: 40, left: 160 };
    const innerWidth = width - margin.left - margin.right;
    const innerHeight = height - margin.top - margin.bottom;

    d3.select(svgRef.current).selectAll('*').remove();

    const svg = d3.select(svgRef.current)
      .attr('width', width)
      .attr('height', height);

    const defs = svg.append('defs');

    pharmaCategories.forEach(cat => {
      const gradient = defs.append('linearGradient')
        .attr('id', `gradient-${cat.id}`)
        .attr('x1', '0%').attr('y1', '0%')
        .attr('x2', '100%').attr('y2', '0%');
      gradient.append('stop').attr('offset', '0%').attr('stop-color', cat.gradient[0]);
      gradient.append('stop').attr('offset', '100%').attr('stop-color', cat.gradient[1]);
    });

    const g = svg.append('g')
      .attr('transform', `translate(${margin.left}, ${margin.top})`);

    const leftScale = d3.scaleBand()
      .domain(chordData.categories.map(d => d.id))
      .range([0, innerHeight])
      .padding(0.15);

    const rightScale = d3.scaleBand()
      .domain(chordData.destinations.map(d => d.id))
      .range([0, innerHeight])
      .padding(0.15);

    const maxVolume = d3.max(chordData.connections, d => d.volume) || 100;

    const getStrokeWidth = (connection) => {
      const baseWidth = (connection.volume / maxVolume) * 20;
      const consumptionMultiplier = {
        over: 1.5,
        normal: 1,
        under: 0.6,
        dead: 0.3
      };
      return Math.max(2, baseWidth * (consumptionMultiplier[connection.consumption] || 1));
    };

    const getOpacity = (consumption) => {
      const opacities = { over: 0.85, normal: 0.7, under: 0.5, dead: 0.3 };
      return opacities[consumption] || 0.7;
    };

    const getConsumptionTint = (consumption, baseColor) => {
      if (consumption === 'over') return d3.color(baseColor).darker(0.3);
      if (consumption === 'under') return d3.color(baseColor).brighter(0.5);
      if (consumption === 'dead') return d3.color('#94a3b8');
      return d3.color(baseColor);
    };

    const linksGroup = g.append('g').attr('class', 'ribbons');

    const createCurvedPath = (source, target, sourceY, targetY, sourceHeight, targetHeight) => {
      const x0 = 0;
      const x1 = innerWidth;
      const controlOffset = innerWidth * 0.4;

      const path = d3.path();
      path.moveTo(x0, sourceY);
      path.bezierCurveTo(
        x0 + controlOffset, sourceY,
        x1 - controlOffset, targetY,
        x1, targetY
      );
      path.lineTo(x1, targetY + targetHeight);
      path.bezierCurveTo(
        x1 - controlOffset, targetY + targetHeight,
        x0 + controlOffset, sourceY + sourceHeight,
        x0, sourceY + sourceHeight
      );
      path.closePath();

      return path.toString();
    };

    const groupedConnections = {};
    chordData.connections.forEach(conn => {
      const key = `${conn.source}-${conn.target}`;
      if (!groupedConnections[key]) {
        groupedConnections[key] = conn;
      }
    });

    Object.values(groupedConnections).forEach(connection => {
      const sourceY = leftScale(connection.source);
      const targetY = rightScale(connection.target);
      if (sourceY === undefined || targetY === undefined) return;

      const strokeWidth = getStrokeWidth(connection);
      const sourceCategory = pharmaCategories.find(c => c.id === connection.source);
      const fillColor = getConsumptionTint(connection.consumption, sourceCategory?.color || '#3b82f6');

      linksGroup.append('path')
        .attr('d', createCurvedPath(
          connection.source, connection.target,
          sourceY + leftScale.bandwidth() / 2 - strokeWidth / 2,
          targetY + rightScale.bandwidth() / 2 - strokeWidth / 2,
          strokeWidth,
          strokeWidth
        ))
        .attr('fill', fillColor)
        .attr('fill-opacity', getOpacity(connection.consumption))
        .style('cursor', 'pointer')
        .on('mouseover', function() {
          d3.select(this).attr('fill-opacity', 0.95);
          setHoveredItem({
            type: 'ribbon',
            source: connection.sourceName,
            target: connection.targetName,
            volume: connection.volume,
            consumption: connection.consumption,
            movement: connection.movement
          });
        })
        .on('mouseout', function() {
          d3.select(this).attr('fill-opacity', getOpacity(connection.consumption));
          setHoveredItem(null);
        });
    });

    const leftNodesGroup = g.append('g').attr('class', 'left-nodes');

    leftNodesGroup.selectAll('.left-bar')
      .data(chordData.categories)
      .join('rect')
      .attr('class', 'left-bar')
      .attr('x', -15)
      .attr('y', d => leftScale(d.id))
      .attr('width', 15)
      .attr('height', leftScale.bandwidth())
      .attr('rx', 4)
      .attr('fill', d => `url(#gradient-${d.id})`)
      .style('cursor', 'pointer')
      .on('mouseover', function(event, d) {
        d3.select(this).attr('opacity', 0.8);
        linksGroup.selectAll('path')
          .attr('fill-opacity', function() {
            const pathData = d3.select(this).datum?.();
            return pathData?.source === d.id ? 0.9 : 0.1;
          });
        setHoveredItem({
          type: 'category',
          name: d.name,
          color: d.color
        });
      })
      .on('mouseout', function() {
        d3.select(this).attr('opacity', 1);
        linksGroup.selectAll('path')
          .each(function(conn) {
            if (conn) {
              d3.select(this).attr('fill-opacity', getOpacity(conn.consumption));
            }
          });
        setHoveredItem(null);
      });

    leftNodesGroup.selectAll('.left-label')
      .data(chordData.categories)
      .join('text')
      .attr('class', 'left-label')
      .attr('x', -22)
      .attr('y', d => leftScale(d.id) + leftScale.bandwidth() / 2)
      .attr('dy', '0.35em')
      .attr('text-anchor', 'end')
      .attr('font-size', '12px')
      .attr('font-weight', '500')
      .attr('fill', '#1e293b')
      .text(d => d.name);

    const rightNodesGroup = g.append('g').attr('class', 'right-nodes');

    rightNodesGroup.selectAll('.right-bar')
      .data(chordData.destinations)
      .join('rect')
      .attr('class', 'right-bar')
      .attr('x', innerWidth)
      .attr('y', d => rightScale(d.id))
      .attr('width', 15)
      .attr('height', rightScale.bandwidth())
      .attr('rx', 4)
      .attr('fill', '#3b82f6')
      .style('cursor', 'pointer')
      .on('mouseover', function(event, d) {
        d3.select(this).attr('opacity', 0.8);
        setHoveredItem({
          type: 'destination',
          name: d.name
        });
      })
      .on('mouseout', function() {
        d3.select(this).attr('opacity', 1);
        setHoveredItem(null);
      });

    rightNodesGroup.selectAll('.right-label')
      .data(chordData.destinations)
      .join('text')
      .attr('class', 'right-label')
      .attr('x', innerWidth + 22)
      .attr('y', d => rightScale(d.id) + rightScale.bandwidth() / 2)
      .attr('dy', '0.35em')
      .attr('text-anchor', 'start')
      .attr('font-size', '12px')
      .attr('font-weight', '500')
      .attr('fill', '#1e293b')
      .text(d => d.name);

  }, [chordData, dimensions, timeframe]);

  const filterOptions = [
    { value: 'areas', label: 'Areas' },
    { value: 'specialities', label: 'Specialities' },
    { value: 'wards', label: 'Wards' }
  ];

  return (
    <div ref={containerRef} className="w-full">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-4">
          <div className="flex items-center gap-2">
            {pharmaCategories.slice(0, 3).map(cat => (
              <div key={cat.id} className="flex items-center gap-1.5">
                <div className="w-3 h-3 rounded" style={{ backgroundColor: cat.color }} />
                <span className="text-xs text-slate-600">{cat.name.split(' ')[0]}</span>
              </div>
            ))}
          </div>
        </div>
        <div className="flex items-center gap-2">
          <span className="text-sm text-slate-500">Filter by:</span>
          <select
            value={filterType}
            onChange={(e) => setFilterType(e.target.value)}
            className="px-3 py-1.5 bg-white border border-slate-200 rounded-lg text-sm font-medium focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            {filterOptions.map(opt => (
              <option key={opt.value} value={opt.value}>{opt.label}</option>
            ))}
          </select>
        </div>
      </div>

      <div className="flex gap-4">
        <div className="flex-1 bg-white rounded-xl border border-slate-200 p-4 relative">
          <div className="absolute top-2 left-4 flex items-center gap-4 text-xs">
            <div className="flex items-center gap-2">
              <span className="font-medium text-slate-600">Movement:</span>
              {[
                { label: 'Fast', color: '#22c55e' },
                { label: 'Medium', color: '#eab308' },
                { label: 'Slow', color: '#f97316' },
                { label: 'Occasional', color: '#ef4444' }
              ].map(item => (
                <div key={item.label} className="flex items-center gap-1">
                  <div className="w-2.5 h-2.5 rounded" style={{ backgroundColor: item.color }} />
                  <span className="text-slate-500">{item.label}</span>
                </div>
              ))}
            </div>
          </div>
          <div className="absolute top-2 right-4 flex items-center gap-4 text-xs">
            <span className="font-medium text-slate-600">Status:</span>
            {[
              { label: 'Over', width: 'w-6' },
              { label: 'Normal', width: 'w-4' },
              { label: 'Under', width: 'w-2' }
            ].map(item => (
              <div key={item.label} className="flex items-center gap-1">
                <div className={`${item.width} h-1.5 bg-slate-400 rounded`} />
                <span className="text-slate-500">{item.label}</span>
              </div>
            ))}
          </div>
          <svg ref={svgRef} className="w-full" style={{ marginTop: '20px' }} />
        </div>

        {hoveredItem && (
          <div className="w-56 bg-slate-900 text-white rounded-xl p-4 shadow-lg self-start">
            <h4 className="font-semibold mb-3 text-blue-300">
              {hoveredItem.type === 'ribbon' ? 'Flow Details' : 
               hoveredItem.type === 'category' ? 'Medicine Category' : 'Destination'}
            </h4>
            {hoveredItem.type === 'ribbon' && (
              <div className="space-y-2 text-sm">
                <p><span className="text-slate-400">From:</span> {hoveredItem.source}</p>
                <p><span className="text-slate-400">To:</span> {hoveredItem.target}</p>
                <p><span className="text-slate-400">Volume:</span> {hoveredItem.volume} units</p>
                <p><span className="text-slate-400">Status:</span> 
                  <span className={`ml-1 px-2 py-0.5 rounded text-xs ${
                    hoveredItem.consumption === 'over' ? 'bg-red-500' :
                    hoveredItem.consumption === 'under' ? 'bg-yellow-500' :
                    hoveredItem.consumption === 'dead' ? 'bg-slate-500' : 'bg-green-500'
                  }`}>
                    {hoveredItem.consumption === 'over' ? 'Over Consumption' :
                     hoveredItem.consumption === 'under' ? 'Under Consumption' :
                     hoveredItem.consumption === 'dead' ? 'Dead Stock' : 'Normal'}
                  </span>
                </p>
              </div>
            )}
            {hoveredItem.type === 'category' && (
              <div className="space-y-2 text-sm">
                <p><span className="text-slate-400">Name:</span> {hoveredItem.name}</p>
                <div className="flex items-center gap-2">
                  <span className="text-slate-400">Color:</span>
                  <div className="w-4 h-4 rounded" style={{ backgroundColor: hoveredItem.color }} />
                </div>
              </div>
            )}
            {hoveredItem.type === 'destination' && (
              <div className="space-y-2 text-sm">
                <p><span className="text-slate-400">Location:</span> {hoveredItem.name}</p>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default PharmaChordDiagram;

API_INTEGRATION.md.content:
# API Integration Guide

## Overview
This document provides guidelines for integrating real backend APIs with the Procurement Model application.

---

## Current State
The application currently uses **mock data** from `src/data/mockData.js`. To connect to a real backend, follow the integration patterns below.

---

## API Endpoints Required

### 1. Products API

#### Get All Products
```
GET /api/products
Query Parameters:
  - category: string (optional)
  - location: string (optional)
  - search: string (optional)
  - limit: number (optional, default: 100)
  - offset: number (optional, default: 0)

Response:
{
  "success": true,
  "data": [
    {
      "id": 1,
      "name": "Fresh Tomatoes",
      "sku": "SKU-001",
      "category": "Fresh Produce",
      "location": "Downtown Hotel",
      "unit": "kg",
      "consumption": 120,
      "expectedConsumption": 100
    }
  ],
  "total": 20,
  "page": 1
}
```

#### Get Product by ID
```
GET /api/products/:id

Response:
{
  "success": true,
  "data": { ... }
}
```

#### Get Product Performance
```
GET /api/products/:id/performance
Query Parameters:
  - timeframe: 'hourly' | 'daily' | 'weekly' | 'monthly' | 'quarterly' | 'yearly'
  - startDate: ISO string (optional)
  - endDate: ISO string (optional)

Response:
{
  "success": true,
  "data": {
    "productId": 1,
    "timeframe": "daily",
    "metrics": [
      {
        "time": "2024-11-24",
        "consumed": 120,
        "expected": 100,
        "variance": 20,
        "status": "over"
      }
    ]
  }
}
```

### 2. RL Products API

#### Get RL Labeled Products
```
GET /api/rl-products
Query Parameters:
  - status: 'pending' | 'approved' | 'rejected' (optional)
  - limit: number (optional, default: 50)
  - offset: number (optional, default: 0)

Response:
{
  "success": true,
  "data": [
    {
      "id": 1,
      "name": "Fresh Tomatoes",
      "sku": "SKU-001",
      "category": "Fresh Produce",
      "suggestedLabel": "Perishable - High Priority",
      "confidence": 92,
      "status": "pending",
      "description": "...",
      "createdAt": "2024-11-24T10:30:00Z",
      "updatedAt": "2024-11-24T10:30:00Z"
    }
  ],
  "total": 10,
  "page": 1
}
```

#### Update RL Product Status
```
POST /api/rl-products/:id/approve
Body:
{
  "approvedBy": "user_id",
  "notes": "Approved as suggested"
}

Response:
{
  "success": true,
  "data": { ... }
}
```

```
POST /api/rl-products/:id/reject
Body:
{
  "rejectedBy": "user_id",
  "reason": "Incorrect label"
}

Response:
{
  "success": true,
  "data": { ... }
}
```

#### Update RL Product Label
```
PUT /api/rl-products/:id
Body:
{
  "name": "Updated Name",
  "suggestedLabel": "Updated Label",
  "description": "Updated description",
  "updatedBy": "user_id"
}

Response:
{
  "success": true,
  "data": { ... }
}
```

### 3. Categories API

#### Get All Categories
```
GET /api/categories

Response:
{
  "success": true,
  "data": [
    "Fresh Produce",
    "Dairy",
    "Meat & Poultry",
    ...
  ]
}
```

### 4. Locations API

#### Get All Locations
```
GET /api/locations

Response:
{
  "success": true,
  "data": [
    "Downtown Hotel",
    "Airport Restaurant",
    ...
  ]
}
```

---

## Implementation Steps

### Step 1: Create API Service Layer

Create `src/services/api.js`:

```javascript
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Products
export const fetchProducts = async (filters = {}) => {
  const params = new URLSearchParams(filters);
  const response = await fetch(`${API_BASE_URL}/products?${params}`);
  if (!response.ok) throw new Error('Failed to fetch products');
  return response.json();
};

export const fetchProductById = async (id) => {
  const response = await fetch(`${API_BASE_URL}/products/${id}`);
  if (!response.ok) throw new Error('Failed to fetch product');
  return response.json();
};

export const fetchProductPerformance = async (id, timeframe) => {
  const response = await fetch(
    `${API_BASE_URL}/products/${id}/performance?timeframe=${timeframe}`
  );
  if (!response.ok) throw new Error('Failed to fetch performance data');
  return response.json();
};

// RL Products
export const fetchRLProducts = async (filters = {}) => {
  const params = new URLSearchParams(filters);
  const response = await fetch(`${API_BASE_URL}/rl-products?${params}`);
  if (!response.ok) throw new Error('Failed to fetch RL products');
  return response.json();
};

export const approveRLProduct = async (id, data) => {
  const response = await fetch(`${API_BASE_URL}/rl-products/${id}/approve`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data),
  });
  if (!response.ok) throw new Error('Failed to approve product');
  return response.json();
};

export const rejectRLProduct = async (id, data) => {
  const response = await fetch(`${API_BASE_URL}/rl-products/${id}/reject`, {
    method: 'POST',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data),
  });
  if (!response.ok) throw new Error('Failed to reject product');
  return response.json();
};

export const updateRLProduct = async (id, data) => {
  const response = await fetch(`${API_BASE_URL}/rl-products/${id}`, {
    method: 'PUT',
    headers: { 'Content-Type': 'application/json' },
    body: JSON.stringify(data),
  });
  if (!response.ok) throw new Error('Failed to update product');
  return response.json();
};

// Categories & Locations
export const fetchCategories = async () => {
  const response = await fetch(`${API_BASE_URL}/categories`);
  if (!response.ok) throw new Error('Failed to fetch categories');
  return response.json();
};

export const fetchLocations = async () => {
  const response = await fetch(`${API_BASE_URL}/locations`);
  if (!response.ok) throw new Error('Failed to fetch locations');
  return response.json();
};
```

### Step 2: Update Components to Use API

Example: Update `ProductJourney.js`:

```javascript
import { useEffect, useState } from 'react';
import { fetchProducts } from '../services/api';

function ProductJourney({ filterType, selectedCategory, selectedLocation, searchQuery }) {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const loadProducts = async () => {
      try {
        setLoading(true);
        const filters = {
          category: selectedCategory !== 'all' ? selectedCategory : undefined,
          location: selectedLocation !== 'all' ? selectedLocation : undefined,
          search: searchQuery || undefined,
        };

        const response = await fetchProducts(filters);
        setProducts(response.data);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    loadProducts();
  }, [selectedCategory, selectedLocation, searchQuery]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  // Rest of component...
}
```

### Step 3: Add Error Handling

Create `src/hooks/useApi.js`:

```javascript
import { useState, useEffect } from 'react';

export const useApi = (apiFunction, dependencies = []) => {
  const [data, setData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        setLoading(true);
        const result = await apiFunction();
        setData(result.data);
        setError(null);
      } catch (err) {
        setError(err.message);
        setData(null);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, dependencies);

  return { data, loading, error };
};
```

### Step 4: Configure Environment Variables

Create `.env`:

```env
REACT_APP_API_URL=http://localhost:5000/api
REACT_APP_API_TIMEOUT=30000
REACT_APP_ENABLE_MOCK_DATA=false
```

### Step 5: Add Loading & Error States

Update components to show:
- Loading spinners
- Error messages
- Retry buttons
- Empty states

---

## Authentication

### Add JWT Token Support

Update `src/services/api.js`:

```javascript
const getAuthHeaders = () => {
  const token = localStorage.getItem('authToken');
  return {
    'Content-Type': 'application/json',
    ...(token && { 'Authorization': `Bearer ${token}` }),
  };
};

export const fetchProducts = async (filters = {}) => {
  const params = new URLSearchParams(filters);
  const response = await fetch(`${API_BASE_URL}/products?${params}`, {
    headers: getAuthHeaders(),
  });
  // ...
};
```

### Login Endpoint

```
POST /api/auth/login
Body:
{
  "email": "user@example.com",
  "password": "password"
}

Response:
{
  "success": true,
  "token": "jwt_token_here",
  "user": { ... }
}
```

---

## Error Handling

### Standard Error Response

```javascript
{
  "success": false,
  "error": {
    "code": "PRODUCT_NOT_FOUND",
    "message": "Product with ID 999 not found",
    "statusCode": 404
  }
}
```

### Handle Errors in Components

```javascript
const handleError = (error) => {
  if (error.statusCode === 401) {
    // Redirect to login
    window.location.href = '/login';
  } else if (error.statusCode === 403) {
    // Show permission denied
    showNotification('Permission denied');
  } else {
    // Show generic error
    showNotification(error.message);
  }
};
```

---

## Caching Strategy

### Add Request Caching

```javascript
const cache = new Map();

export const fetchProductsWithCache = async (filters = {}) => {
  const cacheKey = JSON.stringify(filters);

  if (cache.has(cacheKey)) {
    return cache.get(cacheKey);
  }

  const data = await fetchProducts(filters);
  cache.set(cacheKey, data);

  // Clear cache after 5 minutes
  setTimeout(() => cache.delete(cacheKey), 5 * 60 * 1000);

  return data;
};
```

---

## Rate Limiting

### Implement Request Throttling

```javascript
import { throttle } from 'lodash';

export const throttledSearch = throttle(
  (query) => fetchProducts({ search: query }),
  500
);
```

---

## Testing API Integration

### Mock API for Testing

```javascript
// src/services/mockApi.js
export const mockFetchProducts = async () => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve({ data: mockProductData });
    }, 500);
  });
};
```

### Use in Tests

```javascript
import * as api from '../services/api';
import * as mockApi from '../services/mockApi';

jest.mock('../services/api', () => mockApi);
```

---

## Performance Optimization

### Pagination

```javascript
export const fetchProducts = async (page = 1, limit = 20) => {
  const response = await fetch(
    `${API_BASE_URL}/products?page=${page}&limit=${limit}`
  );
  return response.json();
};
```

### Lazy Loading

```javascript
const [page, setPage] = useState(1);
const [products, setProducts] = useState([]);

const loadMore = async () => {
  const newProducts = await fetchProducts(page + 1);
  setProducts([...products, ...newProducts.data]);
  setPage(page + 1);
};
```

### Infinite Scroll

```javascript
useEffect(() => {
  const handleScroll = () => {
    if (window.innerHeight + window.scrollY >= document.body.offsetHeight) {
      loadMore();
    }
  };

  window.addEventListener('scroll', handleScroll);
  return () => window.removeEventListener('scroll', handleScroll);
}, [page]);
```

---

## Deployment Considerations

### Environment-Specific APIs

```javascript
const API_BASE_URL = {
  development: 'http://localhost:5000/api',
  staging: 'https://staging-api.example.com/api',
  production: 'https://api.example.com/api',
}[process.env.NODE_ENV];
```

### CORS Configuration

Backend should allow:
```
Access-Control-Allow-Origin: http://localhost:3000
Access-Control-Allow-Methods: GET, POST, PUT, DELETE
Access-Control-Allow-Headers: Content-Type, Authorization
```

---

## Monitoring & Logging

### Add Request Logging

```javascript
export const fetchWithLogging = async (url, options = {}) => {
  console.log(`[API] ${options.method || 'GET'} ${url}`);
  const startTime = performance.now();

  try {
    const response = await fetch(url, options);
    const duration = performance.now() - startTime;
    console.log(`[API] Response in ${duration.toFixed(2)}ms`);
    return response;
  } catch (error) {
    console.error(`[API] Error: ${error.message}`);
    throw error;
  }
};
```

---

## Next Steps

1. Set up backend API server
2. Implement endpoints as specified
3. Update `.env` with API URL
4. Replace mock data imports with API calls
5. Add error handling and loading states
6. Test all workflows
7. Deploy to production

---

**For questions or issues, refer to the main README.md and SETUP.md files.**

